ruleExplore.bookList:
class.hoverable||class.bk||class.links-body@tag.li
@js:
list=result.toArray()
json=[]
name="";kind="";chapter="";intro="";cover=""
bookUrl=""
for(i in list){
h=org.jsoup.Jsoup.parse(list[i])

if(baseUrl.match(/yunzhongge/)){
		name=h.select('a').text()
		bookUrl=h.select('a').attr('href')
		kind="云中歌小说网"
	}
else if(baseUrl.match(/xiangcun/)){
		name=h.select('h3 a').text()
		cover=h.select('img').attr('src')
		chapter=h.select('a').last().text()
		intro=h.select('p').text()
		bookUrl=h.select('h3 a').attr('href')
		kind="乡村小说"
	}
else{
		name=h.select('#post-title').text()
		kind=h.select('#post-date').text()
		intro=h.select('#post-content').text()
		bookUrl=h.select('a').attr('href')
	}

json.push(JSON.stringify({
		name:name,
		kind:kind,
		chapter:chapter,
		intro:intro,
		cover:cover,
		bookUrl:bookUrl
	}))
}
if(baseUrl.match(/yunzhongge/)){
	json.unshift(JSON.stringify({"name":"云中歌","kind":kind,"bookUrl":baseUrl}))
}
json

